import React, { useEffect, useState } from 'react'
import { Users } from '../dummyData'


const RightbarProfile = ({user}) => {

  const PF = process.env.REACT_APP_PUBLIC_FOLDER
  console.log("ok", user)
  let userFriends = []

  // capitalize a string
  function capitalize(word) {
    const lower = word.toLowerCase();
    return word.charAt(0).toUpperCase() + lower.slice(1);
  }

  for(let i =0; i<7; i++){
    userFriends.push(          
    <div key={i} className="flex flex-col pt-3">
      <img src={PF+ Users[i].profilePicture} alt="" className="topbarImg cursor-pointer h-28 w-28 rounded object-cover"/>
      <code className="font-bold text-sm">{Users[i].username}</code>
    </div> )
  }
  return (
    <div className='rightbarProfile pt-5 w-1/4 overflow-y-scroll pl-2'>
      <div className="userinfos flex flex-col pb-6">
        <code className='userInformation font-bold mb-2'>User Information</code>
        <code className='userCity'>City: {capitalize(user.city)}</code>
        <code className='userFrom'>From: {capitalize(user.from)}</code>
      </div>

      <div className="userFriends">
        <code className='userFriends font-bold'>User Friends</code>
        <div className="listuserFriends grid grid-cols-2">
          {userFriends}
        </div>
      </div>


    </div>
  )
}

export default RightbarProfile
