import React, {useRef} from 'react'
import { Link } from 'react-router-dom'

const Register = () => {
  const username = useRef(); 
  const email = useRef();
  const password = useRef();
  const passwordAgain = useRef();
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(email)
  };


  return (
    <div className='register w-screen h-screen flex justify-center align-center bg-slate-100'>
      <div className="registerWrapper flex flex-row  w-2/3 h-2/3 self-center">
        <div className="registerLeft flex flex-col w-1/2 pr-10">
            <div className="divForDesignOnly h-2/6"></div>
            <code className="registerLogo text-4xl font-bold">KizBook</code>
            <code className="registerDesc text-2xl">Connect with friends and the world around you on Kizbook.</code>
        </div>
        <form className="registerRight flex flex-col w-1/2 justify-center align-center bg-white" onSubmit={handleSubmit}>
            <input type="text" className="usernameInput h-1/6 p-3 m-3 border-2 border-gray-400 rounded-2xl" placeholder='Username' ref={username}/>
            <input type="text" className="emailInput h-1/6 p-3 m-3 border-2 border-gray-400 rounded-2xl" placeholder="Email" ref={email}/>
            <input type="text" className="passwordInput h-1/6 p-3 m-3 border-2 border-gray-400 rounded-2xl" placeholder='Password' ref={password}/>
            <input type="text" className="passwordAgainInput h-1/6 p-3 m-3 border-2 border-gray-400 rounded-2xl" placeholder='Password Again' ref={passwordAgain} />
            <button className="signUp h-1/6 p-3 m-3 border-2 bg-blue-500 rounded-2xl text-white font-bold text-xl">Sign Up</button>
            <Link to="/login" className='h-1/6 w-1/2 self-center'>
              <button className="register p-3 m-3 border-2 bg-green-500 rounded-2xl text-xl text-white">Log into Account</button>
            </Link>
        </form>
      </div>
    </div>
  )
}

export default Register
